mapper.select:
SELECT ID, WORK_TYPE, ACTION_TIME, KEY_CABINET_NAME FROM NEW_DRIVER_ATTEND_QUIT WHERE NEW_SCHEDULING_ID = #{crId} and DRIVER_ID=#{userId}
and ACTION_TIME>=(TRUNC(SYSDATE-1) + INTERVAL '1' HOUR)
and ACTION_TIME>=(TRUNC(SYSDATE) + INTERVAL '1' HOUR)

select t.ID,t.NEW_TRAIN_NUMBER_ID AS TRAIN_ID,t.CURRENT_STATIONS_ID as STATION_ID, t.STATION_NAME,t.IN_TIME AS arrive,t.OUT_TIME AS depart,t.station_type,t3.TRAIN_NAME from( select t1.ID,t1.NEW_TRAIN_NUMBER_ID,t1.CURRENT_STATIONS_ID,t2.STATION_NAME ,t1.IN_TIME,t1.OUT_TIME,1 AS station_type from (select s.*, row_number() over(partition by s.NEW_TRAIN_NUMBER_ID order by s.IN_TIME ASC) p from NEW_TRAIN_NUMBER_STATION s WHERE s.NEW_TRAIN_NUMBER_ID IN
#{trainId}
AND s.IS_DELETED = 0) t1 LEFT JOIN STATIONS t2 on t1.CURRENT_STATIONS_ID = t2.ID where p < 2 UNION select t1.ID,t1.NEW_TRAIN_NUMBER_ID,t1.CURRENT_STATIONS_ID,t2.STATION_NAME ,t1.IN_TIME,t1.OUT_TIME,2 AS station_type from (select s.*, row_number() over(partition by s.NEW_TRAIN_NUMBER_ID order by s.IN_TIME DESC) p from NEW_TRAIN_NUMBER_STATION s WHERE s.NEW_TRAIN_NUMBER_ID IN
#{trainId}
AND s.IS_DELETED = 0) t1 LEFT JOIN STATIONS t2 on t1.CURRENT_STATIONS_ID = t2.ID where p < 2 ) t LEFT JOIN NEW_TRAIN_NUMBER t3 on t.NEW_TRAIN_NUMBER_ID = t3.ID

mapper.insert:
insert into RECORDEXAMLIST (rel_id,rel_exam_id,rel_answer,rel_correct,rel_re_id, create_time) select ISEQ_RECORDEXAMLIST_ID.nextval, rel_exam_id,rel_answer,rel_correct,rel_re_id,create_time from (
select #{item.relExamId} REL_EXAM_ID, #{item.relAnswer} REL_ANSWER, #{item.relCorrect} REL_CORRECT, #{reId} REL_RE_ID, SYSDATE CREATE_TIME from dual
) A

insert into DRIVER_REPORT_DETAIL ( ID, DRIVER_REPORT_ID, TRAIN_NUMBER_COUNT, START_STATION, RUN_TIME, END_STATION, QUIT_TIME) select ISEQ_DRIVER_REPORT_DETAIL_ID.nextval, DRIVER_REPORT_ID,TRAIN_NUMBER_COUNT,START_STATION,RUN_TIME,END_STATION,QUIT_TIME from (
select #{orderId} DRIVER_REPORT_ID, #{item.trainNum} TRAIN_NUMBER_COUNT, #{item.startStation} START_STATION, #{item.startTime} RUN_TIME, #{item.endStation} END_STATION, #{item.endTime} QUIT_TIME from dual
) A

mapper.update:
update IAM_USER set
FACE_FLAG = '1'
where USER_NUM = #{userNo}
